import React, { useState } from 'react';
import './BMICalculator.css';

const BMICalculator = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [age, setAge] = useState('');
    const [gender, setGender] = useState('male');
    const [heightCm, setHeightCm] = useState('');
    const [weightKg, setWeightKg] = useState('');
    const [bmi, setBmi] = useState(null);
    const [status, setStatus] = useState('');
    const [calories, setCalories] = useState('');

    const calculateBMI = () => {
        const heightInMeters = heightCm / 100;
        const bmiValue = (weightKg / (heightInMeters * heightInMeters)).toFixed(2);
        setBmi(bmiValue);
        
        let status = '';
        if (bmiValue < 18.5) status = 'Underweight';
        else if (bmiValue >= 18.5 && bmiValue < 24.9) status = 'Normal weight';
        else if (bmiValue >= 25 && bmiValue < 29.9) status = 'Overweight';
        else status = 'Obese';
        setStatus(status);

        const bmr = gender === 'male' 
            ? 10 * weightKg + 6.25 * heightCm - 5 * age + 5 
            : 10 * weightKg + 6.25 * heightCm - 5 * age - 161;
        const maintenanceCalories = (bmr * 1.2).toFixed(0);
        setCalories(maintenanceCalories);
    };

    const toggleModal = () => setIsOpen(!isOpen);

    return (
        <div>
            <button className="open-modal-button" onClick={toggleModal}>
                Open BMI Calculator
            </button>
            {isOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h2>BMI Calculator</h2>
                        <div>
                            <label>Age:</label>
                            <input type="number" value={age} onChange={(e) => setAge(e.target.value)} />
                        </div>
                        <div>
                            <label>Gender:</label>
                            <input type="radio" value="male" checked={gender === 'male'} onChange={(e) => setGender(e.target.value)} /> Male
                            <input type="radio" value="female" checked={gender === 'female'} onChange={(e) => setGender(e.target.value)} /> Female
                        </div>
                        <div>
                            <label>Height (cm):</label>
                            <input type="number" value={heightCm} onChange={(e) => setHeightCm(e.target.value)} />
                        </div>
                        <div>
                            <label>Weight (kg):</label>
                            <input type="number" value={weightKg} onChange={(e) => setWeightKg(e.target.value)} />
                        </div>
                        <button className="calculate-button" onClick={calculateBMI}>Calculate</button>
                        {bmi && (
                            <div className="results">
                                <h3>Your BMI: {bmi}</h3>
                                <p>Status: {status}</p>
                                <p>Maintenance Calories: {calories} kcal/day</p>
                            </div>
                        )}
                        <button className="close-modal-button" onClick={toggleModal}>Close</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default BMICalculator;
