import React, { useState, useEffect } from 'react';
import { Chart } from 'primereact/chart';
import './DoughnutChart.css'; // Import your new CSS file

export default function DoughnutChartDemo({ completedTasks, totalTasks }) {
    const [chartData, setChartData] = useState({});
    const [chartOptions, setChartOptions] = useState({});

    useEffect(() => {
        const documentStyle = getComputedStyle(document.documentElement);
        let data = {
            labels: [],
            datasets: [
                {
                    data: [],
                    backgroundColor: [],
                    hoverBackgroundColor: []
                }
            ]
        };

        // Determine the number of completed and remaining tasks
        const completedCount = completedTasks.length;
        const totalCount = tasks.length; // Use the total number of tasks directly
        const remainingCount = totalCount - completedCount;

        if (totalCount === 0) {
            // No tasks
            data.labels = ['No Tasks'];
            data.datasets[0].data = [1]; // Display one segment for "No Tasks"
            data.datasets[0].backgroundColor = ['#e0e0e0']; // Grey color for no tasks
        } else if (completedCount === 0) {
            // All remaining tasks (none completed)
            data.labels = ['Remaining'];
            data.datasets[0].data = [remainingCount]; // Show only remaining tasks
            data.datasets[0].backgroundColor = [
                documentStyle.getPropertyValue('--yellow-500')
            ];
        } else if (remainingCount === 0) {
            // All tasks completed
            data.labels = ['Completed'];
            data.datasets[0].data = [completedCount]; // Show only completed tasks
            data.datasets[0].backgroundColor = [
                documentStyle.getPropertyValue('--blue-500')
            ];
        } else {
            // Both completed and remaining tasks
            data.labels = ['Completed', 'Remaining'];
            data.datasets[0].data = [completedCount, remainingCount];
            data.datasets[0].backgroundColor = [
                documentStyle.getPropertyValue('--blue-500'),
                documentStyle.getPropertyValue('--yellow-500')
            ];
        }

        const options = {
            cutout: '60%', // Cutout for a doughnut shape
            responsive: true, // Responsive design
            maintainAspectRatio: false // Prevent maintaining aspect ratio
        };

        setChartData(data);
        setChartOptions(options);
    }, [completedTasks, tasks]); // Ensure tasks prop is being tracked

    return (
        <div className="doughnut-chart-container">
            <Chart type="doughnut" data={chartData} options={chartOptions} />
            <div className="doughnut-display">Tasks Overview</div>
            <div className="doughnut-footer">
                {totalCount === 0 
                    ? 'No tasks available'
                    : `${completedCount} Completed, ${remainingCount} Remaining`}
            </div>
        </div>
    );
}
