import React, { useState } from "react";
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';

const ToDoList = () => {
    const [tasks, setTasks] = useState(['apple' , 'orange'])

    const handleAddTask = () => {

        const moreItem = document.getElementById('item-id').value;
        document.getElementById('item-id').value = '';
        setTasks([...tasks, moreItem])
    }

    const handleDeleteTask = (index) => {
        setTasks(tasks.filter((_,i) => i !== index))
    }


    return(
        <>
        <h1>TO DO LIST</h1>
        <InputText type="text" placeholder="Enter Task" id="item-id"/>
        <Button label="Submit" icon="pi pi-check" iconPos="right" />
        <button onClick={handleAddTask}>Add Task</button>
        <div>
            <ul>
                {tasks.map((item, index) => <li onClick={() => handleDeleteTask(index)} key={index}>{item}</li>)}
            </ul>
        </div>
        </>
    )

}

export default ToDoList;
