import React from 'react';
import { Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, Tooltip, Legend, ArcElement, Title } from 'chart.js';
import './DoughnutChart.css'; 
ChartJS.register(Tooltip, Legend, ArcElement, Title);

const DoughnutChart = ({ completedTasks, tasks }) => {
   // Calculate the number of completed and uncompleted tasks
    const completedCount = completedTasks.length;
    const uncompletedCount = tasks.length;

    // Chart data
    const data = {
        labels: ['Completed', 'Uncompleted'],
        datasets: [
            {
                data: [completedCount, uncompletedCount],
                backgroundColor: ['#36A2EB', '#FF6384'],
                hoverBackgroundColor: ['#36A2EB', '#FF6384'],
            },
        ],
    };

    // Chart options
    const options = {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
            legend: {
                display: true,
                position: 'top',
                labels: {
                    font: {
                        size: 14,
                        weight: 'bold',
                    },
                    padding: 20,
                },
            },
            tooltip: {
                callbacks: {
                    label: (tooltipItem) => {
                        const label = tooltipItem.label || '';
                        const value = tooltipItem.raw || 0;
                        return `${label}: ${value}`;
                    },
                },
            },
        },
    };

    // Custom plugin to add the count on the chart
    const plugins = [
        {
            id: 'doughnut_labels',
            beforeDraw(chart) {
                const { ctx, data } = chart;
                ctx.save();
    
                const completed = data.datasets[0].data[0];
                const uncompleted = data.datasets[0].data[1];
    
                // Draw the combined text above the doughnut
                ctx.font = 'bold 16px Arial';
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillStyle = '#000';
    
                const centerX = chart.width / 2;
                const centerY = chart.height / 2;
                const offset = -20; // Adjust this value to change vertical position
    
                // Draw combined tasks text
                ctx.fillText(`Tasks: Completed ${completed} | Uncompleted ${uncompleted}`, centerX, centerY + offset);
    
                ctx.restore();
            },
        },
    ];

    return (
        <div className="doughnut-chart-container">
            <Doughnut data={data} options={options} plugins={plugins} />
        </div>
    );
};

export default DoughnutChart;
