import React, { useEffect, useState } from 'react';
import './Clock.css'; 

const TimerZone = () => {
    const [time, setTime] = useState(new Date());

    useEffect(() => {
        const timer = setInterval(() => {
            setTime(new Date());
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    const getHourRotation = () => {
        const hours = time.getHours() % 12;
        const minutes = time.getMinutes();
        return (hours + minutes / 60) * 30;
    };

    const getMinuteRotation = () => {
        const minutes = time.getMinutes();
        return (minutes + time.getSeconds() / 60) * 6;
    };

    const getSecondRotation = () => {
        return time.getSeconds() * 6;
    };

    const renderNumbers = () => {
        const numbers = Array.from({ length: 12 }, (_, i) => i + 1);
        return numbers.map((number) => {
            const angle = (number * 30); // Each number is 30 degrees apart
            const x = 40 * Math.cos((angle * Math.PI) / 180); // Radius for positioning
            const y = 40 * Math.sin((angle * Math.PI) / 180); // Radius for positioning
            return (
                <div
                    key={number}
                    className="clock-number"
                    style={{
                        position: 'absolute',
                        left: '50%',
                        top: '50%',
                        transform: `translate(-50%, -50%) translate(${x}px, ${y}px)`,
                        fontSize: '1rem',
                        color: 'white',
                    }}
                >
                    {number}
                </div>
            );
        });
    };

    return (
        <div className="clock-widget">
            <div className="clock-face">
                {renderNumbers()}
                <div
                    className="hour-hand"
                    style={{ transform: `rotate(${getHourRotation()}deg)` }}
                ></div>
                <div
                    className="minute-hand"
                    style={{ transform: `rotate(${getMinuteRotation()}deg)` }}
                ></div>
                <div
                    className="second-hand"
                    style={{ transform: `rotate(${getSecondRotation()}deg)` }}
                ></div>
            </div>
        </div>
    );
};

export default TimerZone;
