import React from 'react';
import './CircularWidget.css'; // Create this CSS file for styling

const CircularWidget = ({ totalTasks, completedTasks }) => {
    const progress = totalTasks ? (completedTasks / totalTasks) * 100 : 0;

    return (
        <div className="progress-widget">
            <div className="progress-circle" style={{ background: `conic-gradient(#4caf50 ${progress}%, #e0e0e0 ${progress}%)` }}>
                <span className="progress-text">{`${Math.round(progress)}%`}</span>
            </div>
        </div>
    );
};

export default CircularWidgett;
