import React, { useRef, useState } from "react";
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Dropdown } from 'primereact/dropdown';
import { Toast } from 'primereact/toast';
import NavMenu from "./NavMenu";
import CompletedTasks from "./CompletedTasks";

const ToDoList = () => {
    const [tasks, setTasks] = useState([]);
    const [completedTasks, setCompletedTasks] = useState([]);
    const [selectPriority, setSelectPriority] = useState('Low');
    const [isEditing, setIsEditing] = useState(false);
    const [currentTaskIndex, setCurrentTaskIndex] = useState(null);
    const [editValue, setEditValue] = useState('');
    const [view, setView] = useState('uncompleted');
    const toast = useRef(null);

    const priorityOptions = [
        { label: 'Low', value: 'Low' },
        { label: 'Medium', value: 'Medium' },
        { label: 'High', value: 'High' }
    ];

    const handleAddTask = () => {
        const taskText = document.getElementById('item-id').value.trim();
        if (!taskText) {
            return toast.current.show({ severity: 'error', summary: 'Oops!', detail: 'Value cannot be empty' });
        }
        if (tasks.some(task => task.text === taskText)) {
            return toast.current.show({ severity: 'warn', summary: 'Try again!', detail: 'Task already exists' });
        }
        setTasks([...tasks, { text: taskText, priority: selectPriority }]);
        document.getElementById('item-id').value = '';
        setSelectPriority('Low');
        toast.current.show({ severity: 'success', summary: 'Great!', detail: 'Task created successfully' });
    };

    const handleDeleteTask = index => setTasks(tasks.filter((_, i) => i !== index));

    const handleEditTask = (index, task) => {
        setIsEditing(true);
        setCurrentTaskIndex(index);
        setEditValue(task.text);
        setSelectPriority(task.priority);
    };

    const handleSaveTask = index => {
        const updatedTasks = [...tasks];
        updatedTasks[index] = { text: editValue.trim(), priority: selectPriority };
        setTasks(updatedTasks);
        setIsEditing(false);
        setEditValue('');
        setSelectPriority('Low');
    };

    const handleCompleteTask = index => {
        const completedTask = tasks[index];
        setCompletedTasks([...completedTasks, { ...completedTask, completedAt: new Date().toLocaleString() }]);
        handleDeleteTask(index);
        toast.current.show({ severity: 'success', summary: 'Great!', detail: 'Task completed and removed' });
    };

    const handleMenuItemClick = newView => setView(newView);

    const handleDeleteCompletedTask = index => setCompletedTasks(completedTasks.filter((_, i) => i !== index));

    return (
        <div>
            <NavMenu onMenuItemClick={handleMenuItemClick} />
            <div className="todo-container">
                <div className="toDoInput">
                    <h1>TO DO LIST</h1>
                    <InputText type="text" placeholder="Enter Task" id="item-id" className="input-text" />
                    <Dropdown
                        className="input-text"
                        value={selectPriority}
                        options={priorityOptions}
                        onChange={e => setSelectPriority(e.value)}
                        placeholder="Select Priority"
                    />
                    <Button label="Add Task" severity="secondary" raised onClick={handleAddTask} icon="pi pi-check" />
                </div>
                <Toast ref={toast} />
                {tasks.length === 0 ? (
                    <div className="empty-message"><h3>Create a task above</h3></div>
                ) : (
                    <ul className="task-list">
                        {tasks.map((item, index) => (
                            <li className={`task-item ${item.priority.toLowerCase() + '-priority'}`} key={index}>
                                {isEditing && currentTaskIndex === index ? (
                                    <div className="edit-save-container">
                                        <InputText value={editValue} onChange={e => setEditValue(e.target.value)} className="edit-input" />
                                        <Dropdown value={selectPriority} options={priorityOptions} onChange={e => setSelectPriority(e.value)} className="priority-dropdown" />
                                        <Button label="Save" onClick={() => handleSaveTask(index)} />
                                    </div>
                                ) : (
                                    <div className="task-display-container">
                                        <span style={{ width: '150px', display: 'inline-block' }}>
                                            {item.text} ({item.priority})
                                        </span>
                                        <div className="task-buttons">
                                            <Button icon="pi pi-check" onClick={() => handleCompleteTask(index)} aria-label="Complete" />
                                            <Button icon="pi pi-pencil" onClick={() => handleEditTask(index, item)} aria-label="Edit" />
                                            <Button icon="pi pi-times" onClick={() => handleDeleteTask(index)} aria-label="Delete" />
                                        </div>
                                    </div>
                                )}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
            {view === 'completed' && <CompletedTasks completedTasks={completedTasks} onDeleteTask={handleDeleteCompletedTask} />}
        </div>
    );
};

export default ToDoList;
