import React, { useEffect, useRef } from "react";
import TimerZone from "./TimerZone";

const NewsBanner = () => {
    const newsRef = useRef(null);

    useEffect(() => {
        const newsContent = newsRef.current;

        // Set initial animation
        newsContent.style.animation = 'scroll 20s linear infinite';

        // Event handlers for mouse enter and leave
        const handleMouseEnter = () => {
            newsContent.style.animationPlayState = 'paused';
        };

        const handleMouseLeave = () => {
            newsContent.style.animationPlayState = 'running';
        };

        // Add event listeners
        newsContent.addEventListener('mouseenter', handleMouseEnter);
        newsContent.addEventListener('mouseleave', handleMouseLeave);

        // Clean up event listeners on unmount
        return () => {
            newsContent.removeEventListener('mouseenter', handleMouseEnter);
            newsContent.removeEventListener('mouseleave', handleMouseLeave);
        };
    }, []);

    return (
        <div className="news-banner mt-2">
            <div className="news-content" ref={newsRef}>
                {/* Add your news items or content here */}
                <TimerZone />
                <span>Latest Update: Item 2 | </span>
                <span>Hot Topic: Item 3 | </span>
            </div>
        </div>
    )
}
export default NewsBanner;
