import React, { useState } from 'react';
import { Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, Tooltip, Legend, ArcElement, Title, Filler } from 'chart.js';
import './DoughnutChart.css';

ChartJS.register(Tooltip, Legend, ArcElement, Title, Filler);

const DoughnutChart = ({ completedTasks, tasks }) => {
    const [isOpen, setIsOpen] = useState(false); // State to manage chart visibility

    const completedCount = completedTasks.length;
    const uncompletedCount = tasks.length; // Total tasks minus completed tasks

    // Chart data
    const data = {
        labels: ['Completed', 'Uncompleted'],
        datasets: [
            {
                data: [completedCount, uncompletedCount],
                backgroundColor: ['#4CAF50', '#F44336'],
                hoverBackgroundColor: ['#45A049', '#E53935'],
            },
        ],
    };

    const options = {
        maintainAspectRatio: false,
        responsive: true,
    };

    const toggleChart = () => {
        setIsOpen(prev => !prev); // Toggle the chart visibility
    };

    return (
        <div className='doughnut-chart-container'>
            <button onClick={toggleChart} className="widget-button">
                {isOpen ? 'Close Chart' : 'Open Chart'}
            </button>
            {isOpen && (
                <div className="chart-wrapper">
                    <Doughnut data={data} options={options} />
                </div>
            )}
        </div>
    );
};

export default DoughnutChart;
