import React, { useEffect, useState } from 'react';
import './Clock.css'; 

const TimerZone = () => {
    const [time, setTime] = useState(new Date());

    useEffect(() => {
        const timer = setInterval(() => {
            setTime(new Date());
        }, 1000);
        return () => clearInterval(timer);
    }, []);

    const getHourRotation = () => {
        const hours = time.getHours() % 12; // Convert to 12-hour format
        const minutes = time.getMinutes();
        return (hours + minutes / 60) * 30; // 360 degrees / 12 hours = 30 degrees per hour
    };

    const getMinuteRotation = () => {
        const minutes = time.getMinutes();
        return (minutes + time.getSeconds() / 60) * 6; // 360 degrees / 60 minutes = 6 degrees per minute
    };

    const getSecondRotation = () => {
        return time.getSeconds() * 6; // 360 degrees / 60 seconds = 6 degrees per second
    };

    return (
        <div className="clock-widget">
            <div
                className="hour-hand"
                style={{ transform: `rotate(${getHourRotation()}deg)` }}
            ></div>
            <div
                className="minute-hand"
                style={{ transform: `rotate(${getMinuteRotation()}deg)` }}
            ></div>
            <div
                className="second-hand"
                style={{ transform: `rotate(${getSecondRotation()}deg)` }}
            ></div>
            <div className="time">{time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit' })}</div>
        </div>
    )
}
export default TimerZone;
