// DoughnutChart.js
import React, { useState, useEffect } from 'react';
import { Chart } from 'primereact/chart';

const DoughnutChart = ({ completedTasksCount, totalTasks }) => {
    const [chartData, setChartData] = useState({});
    const [chartOptions, setChartOptions] = useState({});

    useEffect(() => {
        const documentStyle = getComputedStyle(document.documentElement);
        const data = {
            labels: ['Completed', 'Remaining'],
            datasets: [
                {
                    data: [completedTasks.length, totalTasks.length],
                    backgroundColor: [
                        documentStyle.getPropertyValue('--blue-500'), 
                        documentStyle.getPropertyValue('--gray-300')
                    ],
                    hoverBackgroundColor: [
                        documentStyle.getPropertyValue('--blue-400'), 
                        documentStyle.getPropertyValue('--gray-200')
                    ]
                }
            ]
        };
        const options = {
            cutout: '60%',
            responsive: true,
            maintainAspectRatio: false // Allow custom dimensions
        };

        setChartData(data);
        setChartOptions(options);
    }, [completedTasks, totalTasks]);

    return (
        <div style={{ width: '120px', height: '120px' }} className="card flex justify-content-center">
            <Chart type="doughnut" data={chartData} options={chartOptions} />
        </div>
    );
}

export default DoughnutChart;
