import React, { useEffect, useState } from "react";
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';

const ToDoList = () => {
    const [tasks, setTasks] = useState(['apple', 'orange'])
    const [errorMessage, setErrorMessage] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [currentTaskIndex, setCurrentTaskIndex] = useState(null);
    const [editValue, setEditValue] = useState('')

    const handleAddTask = () => {

        const moreItem = document.getElementById('item-id').value.trim();
        if (moreItem === '') {
            setErrorMessage('Task cannot be empty!')
            setTimeout(() => setErrorMessage(''), 3000)
            return;
        }
        if (tasks.includes(moreItem)) {
            setErrorMessage('Task already exists')
            setTimeout(() => setErrorMessage(''), 3000)
            return;
        }
        document.getElementById('item-id').value = '';
        setTasks([...tasks, moreItem])
    }

    const handleDeleteTask = (index) => {
        setTasks(tasks.filter((_, i) => i !== index))
    }

    const handleEditTask = (index, task) => {
        setIsEditing(true);
        setCurrentTaskIndex(index);
        setEditValue(task);
    }

    const handleSaveTask = (index) => {
        const updateTasks = [...tasks];
        updateTasks[index] = editValue.trim();
        setTasks(updateTasks);
        setIsEditing(false);
        setEditValue('');
    }


    return (
        <div className="todo-container">
            <div className="toDoInput">
                <h1>TO DO LIST</h1>
                <InputText type="text" placeholder="Enter Task" id="item-id" className="input-text" />
                <Button onClick={handleAddTask} label="Add Task" icon="pi pi-check" iconPos="right" />
            </div>
            {errorMessage && <div className="error-message">{errorMessage}</div>}
            <div className="task-list-container">
                <ul className="task-list">
                    {tasks.map((item, index) => (
                        <li className="task-item" key={index}>
                            {isEditing && currentTaskIndex === index ? (
                                <>
                                    <InputText
                                        value={editValue}
                                        onChange={(e) => setEditValue(e.target.value)}
                                    />
                                    <Button label="Save" onClick={() => handleSaveTask(index)} />
                                </>
                            ) : (
                                <>
                                    <span>{item}</span>
                                    <div className="task-buttons">
                                        <Button
                                            icon="pi pi-pencil"
                                            className="edit-button"
                                            onClick={() => handleEditTask(index, item)}
                                            aria-label="Edit"
                                        />
                                        <Button
                                            icon="pi pi-times"
                                            className="delete-button"
                                            onClick={() => handleDeleteTask(index)}
                                            aria-label="Delete"
                                        />
                                    </div>
                                </>
                            )}
                        </li>
                    ))}

                </ul>
            </div>
        </div>
    )
}

export default ToDoList;
