import React, { useState } from 'react';
import './MoodTracker.css';

const MoodTracker = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [mood, setMood] = useState('😊'); // Default mood emoji
    const [note, setNote] = useState('');
    const [moodHistory, setMoodHistory] = useState([]);

    const handleMoodSelect = (selectedMood) => {
        setMood(selectedMood);
        setMoodHistory([...moodHistory, { mood: selectedMood, note, date: new Date().toLocaleDateString() }]);
        setNote(''); // Clear note after saving mood
        setIsOpen(false); // Close the selection after choosing a mood
    };

    return (
        <div className="mood-tracker">
            <div className="mood-display" onClick={() => setIsOpen(!isOpen)}>
                <span className="mood-icon">{mood}</span>
            </div>
            {isOpen && (
                <div className="emoji-container">
                    {['😊', '😐', '😢', '😡', '😌'].map((emoji) => (
                        <span key={emoji} className="emoji" onClick={() => handleMoodSelect(emoji)}>
                            {emoji}
                        </span>
                    ))}
                    <textarea
                        placeholder="How do you feel today?"
                        value={note}
                        onChange={(e) => setNote(e.target.value)}
                    />
                </div>
            )}
            <div className="mood-history">
                <h3>Mood History</h3>
                <ul>
                    {moodHistory.map((entry, index) => (
                        <li key={index} style={{ color: entry.mood === '😊' ? 'green' : entry.mood === '😢' ? 'blue' : 'red' }}>
                            {entry.date}: {entry.mood} - {entry.note}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default MoodTracker;
