import React, { useState, useEffect } from 'react';
import { Chart } from 'primereact/chart';
import './DoughnutChart.css'; // Import your new CSS file

const DoughnutChart = ({ tasks, completedTasks }) => {
    const [chartData, setChartData] = useState({});
    const [chartOptions, setChartOptions] = useState({});

    useEffect(() => {
        const totalCount = tasks.length;
        const completedCount = completedTasks.length;
        const remainingCount = totalCount - completedCount;

        const data = {
            labels: ['Completed', 'Remaining'],
            datasets: [
                {
                    data: [completedCount, remainingCount],
                    backgroundColor: ['#4caf50', '#ff9800'], // Change to your preferred colors
                    hoverBackgroundColor: ['#388e3c', '#f57c00']
                }
            ]
        };

        setChartData(data);
        setChartOptions({
            cutout: '60%', // Adjust as needed
            responsive: true
        });
    }, [tasks, completedTasks]); // Dependencies to re-run when tasks or completedTasks change

    return (
        <div className="chart-container">
            <Chart type="doughnut" data={chartData} options={chartOptions} />
        </div>
    );
};

export default DoughnutChart;
