import React, { useEffect, useState } from 'react';
import './Clock.css'; 

const TimerZone = () => {
    const [time, setTime] = useState(new Date());

    useEffect(() => {
        const timer = setInterval(() => {
            setTime(new Date());
        }, 1000);

        return () => clearInterval(timer); 
    }, []);

    const formatTime = (date) => {
        return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit' });
    };


    return (
        <div className="clock-widget">
            <div
                className="hour-hand"
                style={{ transform: `rotate(${getHourRotation()}deg)` }}
            ></div>
            <div
                className="minute-hand"
                style={{ transform: `rotate(${getMinuteRotation()}deg)` }}
            ></div>
            <div
                className="second-hand"
                style={{ transform: `rotate(${getSecondRotation()}deg)` }}
            ></div>
            <div className="time">{time.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', second: '2-digit' })}</div>
        </div>
    )
}
export default TimerZone;
