import React from 'react';
import { Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, Tooltip, Legend, ArcElement, Title, Filler } from 'chart.js';
import './DoughnutChart.css'; 
ChartJS.register(Tooltip, Legend, ArcElement, Title, Filler);

const DoughnutChart = ({ completedTasks, tasks }) => {
    const completedCount = completedTasks.length;
    const uncompletedCount = tasks.length;

    const data = {
        labels: ['Completed', 'Uncompleted'],
        datasets: [
            {
                data: [completedCount, uncompletedCount],
                backgroundColor: ['#36A2EB', '#FF6384'],
                hoverBackgroundColor: ['#36A2EB', '#FF6384'],
            },
        ],
    };

    const options = {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
            legend: {
                display: true,
                position: 'bottom', // Moved legend to the bottom for better use of space
                labels: {
                    font: {
                        size: 16,
                        weight: 'bold',
                    },
                    padding: 20,
                },
            },
            tooltip: {
                callbacks: {
                    label: (tooltipItem) => {
                        const label = tooltipItem.label || '';
                        const value = tooltipItem.raw || 0;
                        return `${label}: ${value}`;
                    },
                },
            },
        },
    };

    const plugins = [
        {
            id: 'doughnut_labels',
            beforeDraw(chart) {
                const { ctx, data } = chart;
                ctx.save();

                const completed = data.datasets[0].data[0];
                const uncompleted = data.datasets[0].data[1];

                ctx.font = 'bold 20px Arial'; // Increased font size
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillStyle = '#000';

                const centerX = chart.width / 2;
                const centerY = chart.height / 2;

                // Draw combined tasks text
                ctx.fillText(`Tasks: Completed ${completed} | Uncompleted ${uncompleted}`, centerX, centerY);

                ctx.restore();
            },
        },
    ];

    return (
        <div className="doughnut-chart-container">
            <h3 className="chart-title">Task Completion Overview</h3>
            <Doughnut data={data} options={options} plugins={plugins} />
        </div>
    );
};

export default DoughnutChart;
