import React, { useEffect, useState } from 'react';
import './WeatherWidget.css';

const WeatherWidget = () => {
    const [weatherData, setWeatherData] = useState(null);
    const latitude = 33.4484; // Latitude for Phoenix, AZ
    const longitude = -112.0740; // Longitude for Phoenix, AZ

    const fetchWeather = async () => {
        const response = await fetch(`https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&current_weather=true`);
        const data = await response.json();
        if (data.current_weather) {
            setWeatherData({
                temperature: Math.round(data.current_weather.temperature), // Temperature in Fahrenheit
                city: 'Phoenix', // Set city to Phoenix
            });
        }
    };

    useEffect(() => {
        fetchWeather();
    }, []);

    return (
        <div className="weather-widget">
            {weatherData ? (
                <>
                    <div className="weather-city">{weatherData.city}</div>
                    <div className="weather-temp">{weatherData.temperature}Â°F</div>
                </>
            ) : (
                <div className="loading">Loading...</div>
            )}
        </div>
    );
};

export default WeatherWidget;
