import React from 'react';
import { Doughnut } from 'react-chartjs-2';
import { Chart as ChartJS, Tooltip, Legend, ArcElement, Title, Filler } from 'chart.js';
import './DoughnutChart.css'; 

ChartJS.register(Tooltip, Legend, ArcElement, Title, Filler);

const DoughnutChart = ({ completedTasks, tasks }) => {
    // Calculate the number of completed and uncompleted tasks
    const completedCount = completedTasks.length;
    const uncompletedCount = tasks.length - completedCount; // Ensure accurate uncompleted count

    // Chart data
    const data = {
        labels: [], // Removed labels as per your previous request
        datasets: [
            {
                data: [completedCount, uncompletedCount],
                backgroundColor: ['#36A2EB', '#FF6384'],
                hoverBackgroundColor: ['#36A2EB', '#FF6384'],
            },
        ],
    };

    // Chart options
    const options = {
        maintainAspectRatio: false,
        responsive: true,
        plugins: {
            legend: {
                display: false, // Hide legend
            },
            tooltip: {
                callbacks: {
                    label: (tooltipItem) => {
                        const value = tooltipItem.raw || 0;
                        return `${value}`; // Show only the value
                    },
                },
            },
        },
    };

    return (
        <div className="doughnut-chart-container">
            <Doughnut data={data} options={options} />
            <div className="task-counts">
                <p>Completed Tasks: {completedCount}</p>
                <p>Uncompleted Tasks: {uncompletedCount}</p>
            </div>
        </div>
    );
};

export default DoughnutChart;
